$(document).ready(function () {
    $('#VendorSusunanSaham-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-VendorSusunanSaham-add').modal('show');
        $('#VendorSusunanSaham-create_btn').unbind();
        $('#VendorSusunanSaham-create_btn').on('click', function () {
            addVendorSusunanSahamSave();
        });
    });
});

function addVendorSusunanSahamSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				badanUsaha:$('#Add-VendorSusunanSaham-BadanUsaha').val(),
				civdId:$('#Add-VendorSusunanSaham-CivdId').val(),
				completedDate:$('#Add-VendorSusunanSaham-CompletedDate').val(),
				docNpwp:$('#Add-VendorSusunanSaham-DocNpwp').val(),
				email:$('#Add-VendorSusunanSaham-Email').val(),
				idVendor:$('#Add-VendorSusunanSaham-IdVendor').val(),
				jumlahSaham:$('#Add-VendorSusunanSaham-JumlahSaham').val(),
				nama:$('#Add-VendorSusunanSaham-Nama').val(),
				noKtpKitas:$('#Add-VendorSusunanSaham-NoKtpKitas').val(),
				perorangan:$('#Add-VendorSusunanSaham-Perorangan').is(":checked"),
				wargaNegara:$('#Add-VendorSusunanSaham-WargaNegara').val(),

            }
            RequestData('POST', `/v1/VendorSusunanSaham/add`, '#md-VendorSusunanSaham-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-VendorSusunanSaham-add').modal('hide');
                    getListVendorSusunanSaham();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
