	$('#Edit-VendorSusunanSaham-BadanUsaha').val(data.badanUsaha);
	$('#Edit-VendorSusunanSaham-CivdId').val(data.civdId);
	$('#Edit-VendorSusunanSaham-CompletedDate').val(data.completedDate);
