	$('#Edit-VendorSusunanSaham-JumlahSaham').val(data.jumlahSaham);
	$('#Edit-VendorSusunanSaham-Nama').val(data.nama);
	$('#Edit-VendorSusunanSaham-NoKtpKitas').val(data.noKtpKitas);
	$('#Edit-VendorSusunanSaham-Perorangan').prop('checked', data.perorangan);
	$('#Edit-VendorSusunanSaham-WargaNegara').val(data.wargaNegara);


    $('#md-VendorSusunanSaham-edit').data('id', data.id);
    $('#VendorSusunanSaham-edit_btn').unbind();
