$(document).ready(function () {
    $('#Workflow-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-Workflow-add').modal('show');
        $('#Workflow-create_btn').unbind();
        $('#Workflow-create_btn').on('click', function () {
            addWorkflowSave();
        });
    });
});

function addWorkflowSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				callbackUrl:$('#Add-Workflow-CallbackUrl').val(),
				code:$('#Add-Workflow-Code').val(),
				dataWorkflow:$('#Add-Workflow-DataWorkflow').val(),
				description:$('#Add-Workflow-Description').val(),
				documentNo:$('#Add-Workflow-DocumentNo').val(),
				emailRequester:$('#Add-Workflow-EmailRequester').val(),
				fullnameRequester:$('#Add-Workflow-FullnameRequester').val(),
				groupNo:$('#Add-Workflow-GroupNo').val(),
				idUserRequester:$('#Add-Workflow-IdUserRequester').val(),
				navigationUrl:$('#Add-Workflow-NavigationUrl').val(),
				statusCode:$('#Add-Workflow-StatusCode').val(),
				statusDescription:$('#Add-Workflow-StatusDescription').val(),
				stepNo:$('#Add-Workflow-StepNo').val(),
				subject:$('#Add-Workflow-Subject').val(),
				workflowCode:$('#Add-Workflow-WorkflowCode').val(),

            }
            RequestData('POST', `/v1/Workflow/add`, '#md-Workflow-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-Workflow-add').modal('hide');
                    getListWorkflow();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
