function editWorkflowDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-Workflow-edit').modal('show');

	$('#Edit-Workflow-CallbackUrl').val(data.callbackUrl);
	$('#Edit-Workflow-Code').val(data.code);
	$('#Edit-Workflow-DataWorkflow').val(data.dataWorkflow);
	$('#Edit-Workflow-Description').val(data.description);
	$('#Edit-Workflow-DocumentNo').val(data.documentNo);
	$('#Edit-Workflow-EmailRequester').val(data.emailRequester);
	$('#Edit-Workflow-FullnameRequester').val(data.fullnameRequester);
	$('#Edit-Workflow-GroupNo').val(data.groupNo);
	$('#Edit-Workflow-IdUserRequester').val(data.idUserRequester);
	$('#Edit-Workflow-NavigationUrl').val(data.navigationUrl);
	$('#Edit-Workflow-StatusCode').val(data.statusCode);
	$('#Edit-Workflow-StatusDescription').val(data.statusDescription);
	$('#Edit-Workflow-StepNo').val(data.stepNo);
	$('#Edit-Workflow-Subject').val(data.subject);
	$('#Edit-Workflow-WorkflowCode').val(data.workflowCode);


    $('#md-Workflow-edit').data('id', data.id);
    $('#Workflow-edit_btn').unbind();
    $('#Workflow-edit_btn').on('click', function () {
        editWorkflowSave();
    });
}

function editWorkflowSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				callbackUrl:$('#Edit-Workflow-CallbackUrl').val(),
				code:$('#Edit-Workflow-Code').val(),
				dataWorkflow:$('#Edit-Workflow-DataWorkflow').val(),
				description:$('#Edit-Workflow-Description').val(),
				documentNo:$('#Edit-Workflow-DocumentNo').val(),
				emailRequester:$('#Edit-Workflow-EmailRequester').val(),
				fullnameRequester:$('#Edit-Workflow-FullnameRequester').val(),
				groupNo:$('#Edit-Workflow-GroupNo').val(),
				idUserRequester:$('#Edit-Workflow-IdUserRequester').val(),
				navigationUrl:$('#Edit-Workflow-NavigationUrl').val(),
				statusCode:$('#Edit-Workflow-StatusCode').val(),
				statusDescription:$('#Edit-Workflow-StatusDescription').val(),
				stepNo:$('#Edit-Workflow-StepNo').val(),
				subject:$('#Edit-Workflow-Subject').val(),
				workflowCode:$('#Edit-Workflow-WorkflowCode').val(),

            }
            RequestData('PUT', `/v1/Workflow/edit/${$('#md-Workflow-edit').data('id')}`, '#md-Workflow-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-Workflow-edit').modal('hide');
                    getListWorkflow();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
