$(document).ready(function () {
    $('#WorkflowAttachment-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-WorkflowAttachment-add').modal('show');
        $('#WorkflowAttachment-create_btn').unbind();
        $('#WorkflowAttachment-create_btn').on('click', function () {
            addWorkflowAttachmentSave();
        });
    });
});

function addWorkflowAttachmentSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				idRepository:$('#Add-WorkflowAttachment-IdRepository').val(),
				idWorkflow:$('#Add-WorkflowAttachment-IdWorkflow').val(),

            }
            RequestData('POST', `/v1/WorkflowAttachment/add`, '#md-WorkflowAttachment-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-WorkflowAttachment-add').modal('hide');
                    getListWorkflowAttachment();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
