function editWorkflowAttachmentDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-WorkflowAttachment-edit').modal('show');

	$('#Edit-WorkflowAttachment-IdRepository').val(data.idRepository);
	$('#Edit-WorkflowAttachment-IdWorkflow').val(data.idWorkflow);


    $('#md-WorkflowAttachment-edit').data('id', data.id);
    $('#WorkflowAttachment-edit_btn').unbind();
    $('#WorkflowAttachment-edit_btn').on('click', function () {
        editWorkflowAttachmentSave();
    });
}

function editWorkflowAttachmentSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				idRepository:$('#Edit-WorkflowAttachment-IdRepository').val(),
				idWorkflow:$('#Edit-WorkflowAttachment-IdWorkflow').val(),

            }
            RequestData('PUT', `/v1/WorkflowAttachment/edit/${$('#md-WorkflowAttachment-edit').data('id')}`, '#md-WorkflowAttachment-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-WorkflowAttachment-edit').modal('hide');
                    getListWorkflowAttachment();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
