$(document).ready(function () {
    $('#WorkflowConfig-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-WorkflowConfig-add').modal('show');
        $('#WorkflowConfig-create_btn').unbind();
        $('#WorkflowConfig-create_btn').on('click', function () {
            addWorkflowConfigSave();
        });
    });
});

function addWorkflowConfigSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				active:$('#Add-WorkflowConfig-Active').is(":checked"),
				callbackUrl:$('#Add-WorkflowConfig-CallbackUrl').val(),
				code:$('#Add-WorkflowConfig-Code').val(),
				isSequence:$('#Add-WorkflowConfig-IsSequence').is(":checked"),
				name:$('#Add-WorkflowConfig-Name').val(),
				navigationUrl:$('#Add-WorkflowConfig-NavigationUrl').val(),

            }
            RequestData('POST', `/v1/WorkflowConfig/add`, '#md-WorkflowConfig-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-WorkflowConfig-add').modal('hide');
                    getListWorkflowConfig();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
