function editWorkflowConfigDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-WorkflowConfig-edit').modal('show');

	$('#Edit-WorkflowConfig-Active').prop('checked', data.active);
	$('#Edit-WorkflowConfig-CallbackUrl').val(data.callbackUrl);
	$('#Edit-WorkflowConfig-Code').val(data.code);
	$('#Edit-WorkflowConfig-IsSequence').prop('checked', data.isSequence);
	$('#Edit-WorkflowConfig-Name').val(data.name);
	$('#Edit-WorkflowConfig-NavigationUrl').val(data.navigationUrl);


    $('#md-WorkflowConfig-edit').data('id', data.id);
    $('#WorkflowConfig-edit_btn').unbind();
    $('#WorkflowConfig-edit_btn').on('click', function () {
        editWorkflowConfigSave();
    });
}

function editWorkflowConfigSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				active:$('#Edit-WorkflowConfig-Active').is(":checked"),
				callbackUrl:$('#Edit-WorkflowConfig-CallbackUrl').val(),
				code:$('#Edit-WorkflowConfig-Code').val(),
				isSequence:$('#Edit-WorkflowConfig-IsSequence').is(":checked"),
				name:$('#Edit-WorkflowConfig-Name').val(),
				navigationUrl:$('#Edit-WorkflowConfig-NavigationUrl').val(),

            }
            RequestData('PUT', `/v1/WorkflowConfig/edit/${$('#md-WorkflowConfig-edit').data('id')}`, '#md-WorkflowConfig-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-WorkflowConfig-edit').modal('hide');
                    getListWorkflowConfig();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
