$(document).ready(function () {
    $('#WorkflowConfigDetail-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-WorkflowConfigDetail-add').modal('show');
        $('#WorkflowConfigDetail-create_btn').unbind();
        $('#WorkflowConfigDetail-create_btn').on('click', function () {
            addWorkflowConfigDetailSave();
        });
    });
});

function addWorkflowConfigDetailSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				autoApproveExpired:$('#Add-WorkflowConfigDetail-AutoApproveExpired').val(),
				canAdhoc:$('#Add-WorkflowConfigDetail-CanAdhoc').is(":checked"),
				idUser:$('#Add-WorkflowConfigDetail-IdUser').val(),
				idWorkflowConfig:$('#Add-WorkflowConfigDetail-IdWorkflowConfig').val(),
				isReviewer:$('#Add-WorkflowConfigDetail-IsReviewer').is(":checked"),
				stepName:$('#Add-WorkflowConfigDetail-StepName').val(),
				stepNo:$('#Add-WorkflowConfigDetail-StepNo').val(),

            }
            RequestData('POST', `/v1/WorkflowConfigDetail/add`, '#md-WorkflowConfigDetail-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-WorkflowConfigDetail-add').modal('hide');
                    getListWorkflowConfigDetail();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
