function editWorkflowConfigDetailDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-WorkflowConfigDetail-edit').modal('show');

	$('#Edit-WorkflowConfigDetail-AutoApprovedExpired').val(data.autoApprovedExpired);
	$('#Edit-WorkflowConfigDetail-CanAdhoc').prop('checked', data.canAdhoc);
	$('#Edit-WorkflowConfigDetail-IdUser').val(data.idUser);
	$('#Edit-WorkflowConfigDetail-IdWorkflowConfig').val(data.idWorkflowConfig);
	$('#Edit-WorkflowConfigDetail-IsReviewer').prop('checked', data.isReviewer);
	$('#Edit-WorkflowConfigDetail-StepName').val(data.stepName);
	$('#Edit-WorkflowConfigDetail-StepNo').val(data.stepNo);


    $('#md-WorkflowConfigDetail-edit').data('id', data.id);
    $('#WorkflowConfigDetail-edit_btn').unbind();
    $('#WorkflowConfigDetail-edit_btn').on('click', function () {
        editWorkflowConfigDetailSave();
    });
}

function editWorkflowConfigDetailSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				autoApprovedExpired:$('#Edit-WorkflowConfigDetail-AutoApprovedExpired').val(),
				canAdhoc:$('#Edit-WorkflowConfigDetail-CanAdhoc').is(":checked"),
				idUser:$('#Edit-WorkflowConfigDetail-IdUser').val(),
				idWorkflowConfig:$('#Edit-WorkflowConfigDetail-IdWorkflowConfig').val(),
				isReviewer:$('#Edit-WorkflowConfigDetail-IsReviewer').is(":checked"),
				stepName:$('#Edit-WorkflowConfigDetail-StepName').val(),
				stepNo:$('#Edit-WorkflowConfigDetail-StepNo').val(),

            }
            RequestData('PUT', `/v1/WorkflowConfigDetail/edit/${$('#md-WorkflowConfigDetail-edit').data('id')}`, '#md-WorkflowConfigDetail-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-WorkflowConfigDetail-edit').modal('hide');
                    getListWorkflowConfigDetail();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
