$(document).ready(function () {
    $('#WorkflowDetail-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-WorkflowDetail-add').modal('show');
        $('#WorkflowDetail-create_btn').unbind();
        $('#WorkflowDetail-create_btn').on('click', function () {
            addWorkflowDetailSave();
        });
    });
});

function addWorkflowDetailSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				autoApprovedExpired:$('#Add-WorkflowDetail-AutoApprovedExpired').val(),
				canAdhoc:$('#Add-WorkflowDetail-CanAdhoc').is(":checked"),
				email:$('#Add-WorkflowDetail-Email').val(),
				fullName:$('#Add-WorkflowDetail-FullName').val(),
				groupNo:$('#Add-WorkflowDetail-GroupNo').val(),
				idUser:$('#Add-WorkflowDetail-IdUser').val(),
				idWorkflow:$('#Add-WorkflowDetail-IdWorkflow').val(),
				isAdhoc:$('#Add-WorkflowDetail-IsAdhoc').is(":checked"),
				isReviewer:$('#Add-WorkflowDetail-IsReviewer').is(":checked"),
				stepName:$('#Add-WorkflowDetail-StepName').val(),
				stepNo:$('#Add-WorkflowDetail-StepNo').val(),

            }
            RequestData('POST', `/v1/WorkflowDetail/add`, '#md-WorkflowDetail-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-WorkflowDetail-add').modal('hide');
                    getListWorkflowDetail();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
