function editWorkflowDetailDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-WorkflowDetail-edit').modal('show');

	$('#Edit-WorkflowDetail-AutoApproveExpired').val(data.autoApproveExpired);
	$('#Edit-WorkflowDetail-CanAdhoc').prop('checked', data.canAdhoc);
	$('#Edit-WorkflowDetail-Email').val(data.email);
	$('#Edit-WorkflowDetail-FullName').val(data.fullName);
	$('#Edit-WorkflowDetail-GroupNo').val(data.groupNo);
	$('#Edit-WorkflowDetail-IdUser').val(data.idUser);
	$('#Edit-WorkflowDetail-IdWorkflow').val(data.idWorkflow);
	$('#Edit-WorkflowDetail-IsAdhoc').prop('checked', data.isAdhoc);
	$('#Edit-WorkflowDetail-IsReviewer').prop('checked', data.isReviewer);
	$('#Edit-WorkflowDetail-StepName').val(data.stepName);
	$('#Edit-WorkflowDetail-StepNo').val(data.stepNo);


    $('#md-WorkflowDetail-edit').data('id', data.id);
    $('#WorkflowDetail-edit_btn').unbind();
    $('#WorkflowDetail-edit_btn').on('click', function () {
        editWorkflowDetailSave();
    });
}

function editWorkflowDetailSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				autoApproveExpired:$('#Edit-WorkflowDetail-AutoApproveExpired').val(),
				canAdhoc:$('#Edit-WorkflowDetail-CanAdhoc').is(":checked"),
				email:$('#Edit-WorkflowDetail-Email').val(),
				fullName:$('#Edit-WorkflowDetail-FullName').val(),
				groupNo:$('#Edit-WorkflowDetail-GroupNo').val(),
				idUser:$('#Edit-WorkflowDetail-IdUser').val(),
				idWorkflow:$('#Edit-WorkflowDetail-IdWorkflow').val(),
				isAdhoc:$('#Edit-WorkflowDetail-IsAdhoc').is(":checked"),
				isReviewer:$('#Edit-WorkflowDetail-IsReviewer').is(":checked"),
				stepName:$('#Edit-WorkflowDetail-StepName').val(),
				stepNo:$('#Edit-WorkflowDetail-StepNo').val(),

            }
            RequestData('PUT', `/v1/WorkflowDetail/edit/${$('#md-WorkflowDetail-edit').data('id')}`, '#md-WorkflowDetail-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-WorkflowDetail-edit').modal('hide');
                    getListWorkflowDetail();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
