$(document).ready(function () {
    getListWorkflowDetail();
});

function getListWorkflowDetail(page) {
    page = page != undefined ? page : 1;
    var pageSize = $('#WorkflowDetail-page_select').val();
    var element = {
        table: '#WorkflowDetail-table',
        tbody: '#WorkflowDetail-tbody',
        from: '#WorkflowDetail-from_page',
        to: '#WorkflowDetail-to_page',
        total: '#WorkflowDetail-total',
        pagination: '#WorkflowDetail-pagination',
        item_pagination: 'WorkflowDetail-item'
    }
    var param = {
        sort: {
            field: "id",
            type: 0
        },
        start: page,
        length: pageSize
    }
    RequestData('POST', "/v1/WorkflowDetail/list", element.table, element.tbody, JSON.stringify(param), function (data) {
        if (data.succeeded) {
            $(element.tbody).html('');
            $(element.pagination).html('');
            if (data.list.length > 0) {
                SetTableData(true, 12, element, {
                    page: page,
                    pageSize: pageSize,
                    count: data.count,
                    function_name: 'getListWorkflowDetail'
                }, function (count) {
                    data.list.forEach(function (item) {
                        $(element.tbody).append(`
                            <tr>
                                <td class="text-nowrap text-center">${count}</td>
								<td class="text-nowrap">${item.autoApproveExpired}</td>
								<td class="text-nowrap">${item.canAdhoc}</td>
								<td class="text-nowrap">${item.email}</td>
								<td class="text-nowrap">${item.fullName}</td>
								<td class="text-nowrap">${item.groupNo}</td>
								<td class="text-nowrap">${item.idUser}</td>
								<td class="text-nowrap">${item.idWorkflow}</td>
								<td class="text-nowrap">${item.isAdhoc}</td>
								<td class="text-nowrap">${item.isReviewer}</td>
								<td class="text-nowrap">${item.stepName}</td>
								<td class="text-nowrap">${item.stepNo}</td>

                                <td class="text-center">
                                    <div>
                                        <button type="button" data-toggle="dropdown" class="btn btn-sm btn-info dropdown-toggle" aria-hashpopup="true" aria-expanded="false">Aksi</button>
                                        <div class="dropdown-menu">
                                            <a href="#!" class="dropdown-item" onclick="detailWorkflowDetailDialog(this);" data-detail='${JSON.stringify(item).replace(/' /g, " " ) }'>Detail</a>
                                            <a href="#!" class="dropdown-item" onclick="editWorkflowDetailDialog(this);" data-detail='${JSON.stringify(item).replace(/' /g, " " ) }'>Edit</a>
                                            <a href="#!" class="dropdown-item" onclick="deleteWorkflowDetailDialog(${item.id})">Delete</a>
                                        </div>
                                    </div>
                                </td>
                            </tr>
                        `);
                        count++;
                    });
                });
            } else {
                settabledata(false, 12, element);
            }
        } else {
            ShowNotif(`${data.message} : ${data.description}`, "error");
        }
    })
}

