$(document).ready(function () {
    $('#WorkflowLog-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-WorkflowLog-add').modal('show');
        $('#WorkflowLog-create_btn').unbind();
        $('#WorkflowLog-create_btn').on('click', function () {
            addWorkflowLogSave();
        });
    });
});

function addWorkflowLogSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				email:$('#Add-WorkflowLog-Email').val(),
				fullname:$('#Add-WorkflowLog-Fullname').val(),
				groupNo:$('#Add-WorkflowLog-GroupNo').val(),
				idUser:$('#Add-WorkflowLog-IdUser').val(),
				idWorkflow:$('#Add-WorkflowLog-IdWorkflow').val(),
				idWorkflowStatus:$('#Add-WorkflowLog-IdWorkflowStatus').val(),
				isAdhoc:$('#Add-WorkflowLog-IsAdhoc').is(":checked"),
				isReviewer:$('#Add-WorkflowLog-IsReviewer').is(":checked"),
				notes:$('#Add-WorkflowLog-Notes').val(),
				statusDescription:$('#Add-WorkflowLog-StatusDescription').val(),
				stepName:$('#Add-WorkflowLog-StepName').val(),
				stepNo:$('#Add-WorkflowLog-StepNo').val(),

            }
            RequestData('POST', `/v1/WorkflowLog/add`, '#md-WorkflowLog-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-WorkflowLog-add').modal('hide');
                    getListWorkflowLog();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
