function editWorkflowLogDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-WorkflowLog-edit').modal('show');

	$('#Edit-WorkflowLog-Email').val(data.email);
	$('#Edit-WorkflowLog-FullName').val(data.fullName);
	$('#Edit-WorkflowLog-GroupNo').val(data.groupNo);
	$('#Edit-WorkflowLog-IdUser').val(data.idUser);
	$('#Edit-WorkflowLog-IdWorkflow').val(data.idWorkflow);
	$('#Edit-WorkflowLog-IdWorkflowStatus').val(data.idWorkflowStatus);
	$('#Edit-WorkflowLog-IsAdhoc').prop('checked', data.isAdhoc);
	$('#Edit-WorkflowLog-IsReviewer').prop('checked', data.isReviewer);
	$('#Edit-WorkflowLog-Notes').val(data.notes);
	$('#Edit-WorkflowLog-StatusDescription').val(data.statusDescription);
	$('#Edit-WorkflowLog-StepName').val(data.stepName);
	$('#Edit-WorkflowLog-StepNo').val(data.stepNo);


    $('#md-WorkflowLog-edit').data('id', data.id);
    $('#WorkflowLog-edit_btn').unbind();
    $('#WorkflowLog-edit_btn').on('click', function () {
        editWorkflowLogSave();
    });
}

function editWorkflowLogSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				email:$('#Edit-WorkflowLog-Email').val(),
				fullName:$('#Edit-WorkflowLog-FullName').val(),
				groupNo:$('#Edit-WorkflowLog-GroupNo').val(),
				idUser:$('#Edit-WorkflowLog-IdUser').val(),
				idWorkflow:$('#Edit-WorkflowLog-IdWorkflow').val(),
				idWorkflowStatus:$('#Edit-WorkflowLog-IdWorkflowStatus').val(),
				isAdhoc:$('#Edit-WorkflowLog-IsAdhoc').is(":checked"),
				isReviewer:$('#Edit-WorkflowLog-IsReviewer').is(":checked"),
				notes:$('#Edit-WorkflowLog-Notes').val(),
				statusDescription:$('#Edit-WorkflowLog-StatusDescription').val(),
				stepName:$('#Edit-WorkflowLog-StepName').val(),
				stepNo:$('#Edit-WorkflowLog-StepNo').val(),

            }
            RequestData('PUT', `/v1/WorkflowLog/edit/${$('#md-WorkflowLog-edit').data('id')}`, '#md-WorkflowLog-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-WorkflowLog-edit').modal('hide');
                    getListWorkflowLog();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
